import { useEffect } from "react";
import "./css/nav.css"
import ItemCount from "./ItemCount"




function ItemListContainer(props){

    function productosAPI(){
        return new Promise(
        function mostrar(resolve){
            const arrayProductos = [{
                id: "1",
                nombre : "producto 1",
                stock : 5,
                descripcion : "El producto mas vendido de la tienda"
            }];
            setTimeout(
                ()=> resolve(arrayProductos),3000)
        }
    )
    }
    async function mostrarApi(){              
        let mostrar = await productosAPI()
        console.log(mostrar)
    }
    mostrarApi()

    
    useEffect(
        ()=>{
            productosAPI()
        }
    )
    
    
    return(
        <div className="container">
            <h1>{props.saludo}</h1>
            <div>   
            <ItemCount stock={5} initial={1}/>
            </div>
        </div>
        
    )
}

export default ItemListContainer;
