import React, {useState} from 'react'
import {createBuyOrder} from "../firebase/firestore"

    
function CartForm({cart,clear,totalPrice}) {
    const [buyer, Setbuyer] = useState(
        {
            nombre : "",
            mail: "",
            celular : ""

        }

    );

    const handleChange = (evt) => {

        const field = evt.target.name;
        const value = evt.target.value;
        Setbuyer({
            ...buyer,
            [field] : value,
        })


    }



    function handlebuyer(){
        const dataOrder = {
            buyer,
            items : cart,
            total : totalPrice()
        }
    
    
        createBuyOrder(dataOrder).then( (orderCreated) =>  {
            clear();  //limpia el carrito dsp de finalizar la compra para que no se duplique lo elegido
            console.log(orderCreated.id);
        } )
    }
    

    return (
        <form>
        <label htmlFor="nombre">Usuario</label>
        <input onChange={handleChange} name="nombre"></input>
    
        <label htmlFor="mail">Mail</label>
        <input onChange={handleChange} name="mail"></input>
    
        <label htmlFor="phone">Celular</label>
        <input onChange={handleChange} name="celular"></input>
    
        <button onClick={handlebuyer}>Finalizar!</button>
        </form>
    )
    }

    
    



export default CartForm;

