import "./css/nav.css"
import ItemCount from "./ItemCount"
import arrayProductos from "./item"
import Item from "./ItemList";



function ItemListContainer(props){

    // useEffect(                                                             //use efect para que cuando se actualiza la pagina lo hagao d nuevo 
    //     ()=>{ 
    //         productosAPI().then(
    //             function mostrarApi(respuesta){
    //                 return(respuesta)
    //             }
    //         )
    //     }
    // )
    
    return(
        <div className="container">
            <h1>{props.saludo}</h1>
            <div>   
                {arrayProductos.map(Item => {
                    return( <Item/> )
                })}
            <ItemCount stock={5} initial={1}/>
            </div>
        </div>
        
    )
}

export default ItemListContainer;
