import React, { useState } from 'react'
import { useForm } from "react-hook-form";


export const CartForm = ({cart,clear, totalPrice,createBuyOrder}) => {

    const { register, handleSubmit, formState: {errors} } = useForm();
    

    const [comprador, setCompador] = useState({
        name: "",
        phone: "",
        email: "",
    }
    )


    const handleChange = (evt) => {
        const field = evt.target.name;
        const value = evt.target.value;
        setCompador({ 
            ...comprador, [field]: value 
        });
    }

    function handleBuyOrder(evt) {
        
        const dataOrder = {
            comprador,
            items: cart,
            total: totalPrice()
        }

        createBuyOrder(dataOrder).then((orderCreated) => {
            clear();
            alert("Ticket de compra: " + orderCreated.id);
        });

    }


    return (<>
    
        <div>
            <div>
                <h1>Ingrese sus datos</h1>
            </div>
            <div>
                <form onSubmit={handleSubmit(handleBuyOrder)}>
                    <label htmlFor="name">Nombre</label>
                    <input {...register("name", { required: true})} aria-required  onChange={handleChange} type="text" name="name"   />
                    {errors.name?.type === 'required' && <span>Se requiere un nombre de contacto</span>}

                    <label htmlFor="phone">Telefono</label>
                    <input {...register("phone", { required: true})} aria-required  onChange={handleChange} type="phone" name="phone"  />
                    {errors.phone?.type === 'required' && <span>Se requiere un nombre de contacto</span>}

                    <label  htmlFor="email">Email</label>
                    <input {...register("email", { required: true, pattern: /\S+@\S+\.\S+/ })} onChange={handleChange} type="email" name="email"  />
                    {errors.email?.type === 'required' && <span>Se requiere un Email</span>}
                    {errors.email?.type === 'pattern' && <span> Invalid email</span>}

                    <button type="submit" >Finalizar Compra</button>
                </form>
            </div>

        </div>
        </>
    )
}

export default CartForm;
