//         <div class="carritoAcumulado">
//             {cart.map((item) => (

//                 <><h3> {item.nombre} </h3><p> {item.descripcion} </p>
//                 <p>Cantidad de unidades: {contarItems()}</p>
//                 <em> El precio de Unidad es de {item.precio} </em>
//                 <button onClick={() => removeItem(item.id)} className="borrarItemCarrito">Borrar el ultimo elemento agregado</button>
//                 <div>
//                 <p>total: {totalPrice().toFixed(2)}{" "}</p><button onClick={handlebuyer}>Finalizar Compra</button>
//                 <button onClick={() => clear()} className="vaciarCarrito">Vaciar carrito</button>
//                 </div></>


//                     ))}
//         </div>

// )

return (
    <>
      <div className="container px-5 py-24 mx-auto flex sm:flex-nowrap flex-wrap">
        {cart.map((item) => (
          <div
            key={item.id}
            className="bg-gray-300 rounded-lg overflow-hidden sm:mr-10 p-10 flex items-end justify-start relative"
          >
            <div className="mb-5 py-3 text-center">
              <h3 className="text-3xl text-gray-900 mt-2 mb-4 font-medium title-font">
                {item.name}
              </h3>
              <p className="mt-4 text-green-700 text-md font-bold">
                $ {item.price}
              </p>
              <p>Cantidad de unidades: {item.qnty} </p>
              <button
                onClick={() => removeItem(item.id)}
                className="bg-red-500 py-2 px-8 rounded-md font-bold text-white mt-5"
              >
                X
              </button>
              <p>
                Subtotal:{" "}
                <span className="text-green-600">
                  ${item.price * item.qnty}
                </span>
              </p>
            </div>
          </div>
        ))}
      </div>
      <div className="container px-5 py-24 mx-auto flex sm:flex-nowrap flex-wrap">
        <div>
          <p className="text-2x1">
            Total a pagar: ${totalPriceCart().toFixed(2)}{" "}
          </p>
        </div>

       <CartForm cart={cart} totalPriceCart={totalPriceCart} clearCart={clearCart} createBuyOrder={createBuyOrder} />
      </div>
    </>
  );
}




export default CartReview;
