import React, {useState} from 'react'
import inputForm from './inputForm';

    
function CartForm( {cart,clear, totalPrice,createBuyOrder}) {

    const [comprador, setComprador] = useState(
        {
            name:" ",
            email:" ",
            phone:" ",

        },
    );

    const handleChange = (evt) => {
        const input= evt.target.name;
        const value = evt.target.value;
        setComprador({
            ...comprador,
            [input] : value,
        })}



    function handlebuyer(evt){
        evt.preventDefault();
        const dataOrder = {
            comprador,
            items : cart,
            total : totalPrice(),
        }
    
    
        createBuyOrder(dataOrder).then( (orderCreated) =>  {
            clear();  //limpia el carrito dsp de finalizar la compra para que no se duplique lo elegido
            alert("Tu numero de orden de compra es : " + orderCreated.id + "  Revisa su casilla de mail para m치s imformaci칩n");
        } )
    }


    return (
        <inputForm  text="Nombre" type="name"  onCahange={handleChange}/>


    )
    }

export default CartForm;
