function Final({cart,clear, totalPrice,createBuyOrder}){

        const [comprador, setComprador] = useState(
            {
                name:" ",
                email:" ",
                phone:" ",
    
            },
        );
    
    
        const handleChange = (evt) => {
            const input= evt.target.name;
            const value = evt.target.value;
            setComprador({
                ...comprador,
                [input] : value,
            })}
    
    
    
        function handlebuyer(evt){
            evt.preventDefault();
            const dataOrder = {
                comprador,
                items : cart,
                total : totalPrice(),
            }
    
        
        
        
            createBuyOrder(dataOrder).then( (orderCreated) =>  {
                clear();  //limpia el carrito dsp de finalizar la compra para que no se duplique lo elegido
                console.log(orderCreated.id)
            } )}
    
        
    return (
        <div>
            Compra finalizada. chequea tu casilla de mail para corroborrar los detalles de tu compra!
        </div>
    )
}

export default Final;
