function Count(){
    const [suma, setSuma] = useState("suma");

    function Suma(){
        setSuma(suma + 1);
    }
    return(
        <div className="div-boton">
            <button class="boton" >-</button>
            <span>0</span>
            <button className="boton" onClick={suma}>+</button>
            <button>Agregar al carrito</button>
        </div>
    )
}

export default Count;
