import {useState} from "react";

function ItemCount({stock,initial}){
    const [cant, setCount] = useState(initial);

    if (stock > 5){
        alert("llegaste al maximo de unidades disponibles")

    }

    function Suma(){
        console.log("una suma")
        setCount(cant + 1);
    }

    function Resta(){
        console.log("una resta")
        setCount(cant - 1);
    }
    return(
        <div className="div-boton">
            <button class="boton" onClick={Resta}>-</button>
            <span>{cant}</span>
            <button className="boton" onClick={Suma}>+</button>
            <button>Agregar al carrito</button>
        </div>
    )
}

export default ItemCount;
