import "./css/nav.css"
import ItemCount from "./ItemCount"
import mapeo from "./itemList"


function ItemListContainer(props){

    function productosAPI(){                                                       //promesa
        return new Promise(
        function mostrar(resolve){
            const arrayProductos = [{
                id: "1",
                nombre : "producto 1",
                stock : 5,
                descripcion : "El producto mas vendido de la tienda"
            }];
            setTimeout(
                ()=> resolve(arrayProductos),3000)
        }
    )
    }

    useEffect(                                                             //use efect para que cuando se actualiza la pagina lo hagao d nuevo 
        ()=>{ 
            productosAPI().then(
                function mostrarApi(respuesta){
                    console.log(respuesta)
                }
            )
        }
    )
    
