import React, {useState} from 'react'
import {createBuyOrder} from "../firebase/firestore"

    
function CartForm() {

    const {cart,clear,totalPrice} = useContext(cartContext);


    function handlebuyer(){
        const dataOrder = {
            buyer :{
                name: "react37",
                phone: "1122233343",
                email : "ferreirabelenn@gmail.com"
            },
            items : cart,
            total : totalPrice()
        }
    
    
        createBuyOrder(dataOrder).then( (orderCreated) =>  {
            clear();  //limpia el carrito dsp de finalizar la compra para que no se duplique lo elegido
            console.log(orderCreated.id);
        } )
    }
    

    return (
        <form>
        <label htmlFor="username">Usuario</label>
        <input onChange={handleChange} name="name"></input>
    
        <label htmlFor="phone">Telefono</label>
        <input onChange={handleChange} name="phone"></input>
    
        <label htmlFor="email">Email</label>
        <input onChange={handleChange} name="email"></input>
    
        <button onClick={handlebuyer}>Finalizar!</button>
        </form>
    )
    }

    
    



