function productos(){
    return new Promise(
        function mostrar(resolve){
            setTimeout(
                () => resolve(
                    array = [{
                        id:1,
                        nombre: "belen"
                    }],3000
                )
            )
        }
    )
}

productos().then(
    function oa(respuesta){
        console.log(respuesta)
    }
)
