export function productosAPI(){
    return new Promise(
    function mostrar(resolve){
        const data = {
            id: "1",
            nombre : "producto 1",
            stock : 5,
            descripcion : "El producto mas vendido de la tienda"
        };
        setTimeout(
            ()=> resolve(data),3000)
    }
)
}

export async function mostrarApi(){              
    let mostrar = await productosAPI()
    console.log(mostrar)
}

mostrarApi()
